# quick & dirty import
crashes = read_csv("crashes_2014_2023.csv")
# mapping crashes in October-2018
crashes |&gt; 
  filter(ACCIDENT_YEAR == 2018) |&gt; 
  mutate(MONTH = month(COLLISION_DATE)) |&gt; 
  filter(MONTH == 10) |&gt; 
  leaflet() |&gt; 
  addTiles() |&gt; 
  addCircles(lng = ~POINT_X, 
             lat = ~POINT_Y,
             label = ~TYPE_OF_COLLISION)

Mapping Ideas
Color code by TYPE_OF_COLLISION
# create color palette
collision_pal = colorFactor(
  palette = "Set1",
  domain = crashes$TYPE_OF_COLLISION)

# mapping crashes in Alameda, Aug-Oct 2018
crashes |&gt; 
  filter(ACCIDENT_YEAR == 2018) |&gt; 
  mutate(MONTH = month(COLLISION_DATE)) |&gt; 
  filter(MONTH %in% 8:12, COUNTY == "ALAMEDA") |&gt; 
  leaflet() |&gt; 
  addProviderTiles("CartoDB.Positron") |&gt; 
  addCircles(lng = ~POINT_X, 
             lat = ~POINT_Y,
             label = ~TYPE_OF_COLLISION,
             color = ~collision_pal(TYPE_OF_COLLISION)) |&gt; 
  addLegend(position = "bottomleft",
            pal = collision_pal, 
            values = ~TYPE_OF_COLLISION)

Example
Here’s an example of a choropleth map to visualize the proportion of DUI crashes in 2018
# quick & dirty import
crashes = read_csv("crashes_2014_2023.csv")

crashes_total_2018_by_county = crashes |&gt; 
  filter(ACCIDENT_YEAR == 2018) |&gt; 
  group_by(COUNTY) |&gt; 
  count(COUNTY, name = "total")

crashes_dui_2018_by_county = crashes |&gt; 
  filter(ACCIDENT_YEAR == 2018, PCF_VIOL_CATEGORY == "DUI") |&gt; 
  group_by(COUNTY) |&gt; 
  count(COUNTY, name = "dui")

dui_2018_by_county = crashes_dui_2018_by_county |&gt; 
  inner_join(crashes_total_2018_by_county, by = "COUNTY") |&gt; 
  mutate(prop = dui / total,
         county = str_to_lower(COUNTY))

# color palette
pal &lt;- colorNumeric(
  palette = "viridis",
  domain = c(0, unique(dui_2018_by_county$prop)),
  reverse = TRUE)

# leaflet choropleth map
cal_counties |&gt; 
  inner_join(dui_2018_by_county, by = "county") |&gt; 
  leaflet() |&gt; 
  addProviderTiles("CartoDB.Positron") |&gt; 
  addPolygons(color = ~pal(prop),
              fillOpacity = 0.7,
              weight = 1,
              label = ~county) |&gt; 
  addLegend(position = "bottomleft", 
            pal = pal,
            values = ~prop,
            title = "DUI Prop",
            opacity = 1)
